/* eslint no-console: 0 */

const term = require('../../util/term');
const cds = require('@sap/cds/lib');
const LOG = cds.log('init');

const defaultLogger = {
    isDebug: LOG._debug,

    debug: (message = '') => {
        LOG.debug(message);
    },

    log: (message = '') => {
        LOG.log(message);
    },

    warn: (message = '') => {
        LOG.warn(term.warn(message));
    },

    error: (message = '') => {
        LOG.error(term.error(message));
    }
}

defaultLogger.isTTY = process.stdout.isTTY;

const nullLogger = {
    debug: () => { },
    log: () => { },
    warn: () => { },
    error: () => { },
    write: () => { }
}

module.exports = {
    defaultLogger,
    nullLogger
}
