/* eslint-disable no-console */
class ClientLog {

    static log(logText) {
        console.log(logText);
    }

    static error(errorText, error) {
        console.error(this.getMessage(errorText, error));
    }

    static debug(debugText, error) {
        if (process.env.DEBUG) {
            console.error(this.getMessage(debugText, error));
        }
    }

    static getMessage(errorText, error) {
        let message = errorText && errorText + '\n' || '';
        if (!error) {
            return message;
        }
        if (process.env.DEBUG) {
            if (error.response && error.response.body) {
                try {
                    const body = JSON.parse(error.response.body);
                    if (body.error && body.error.message) {
                        message += body.error.message + '\n';
                    }
                } catch (parseError) {
                    message += error.response.body + '\n';
                }
            }
            message += '\n' + error.stack;
        } else {
            message += error.message;
        }
        return message;
    }
}

module.exports = ClientLog;
